fun floor(direction: String) =
    direction
        .map { if (it == '(') 1 else -1 }
        .sum()

fun firstBasement(direction: String): Int {
    direction
        .map { if (it == '(') 1 else -1 }
        .reduceIndexed { i, acc, v ->
            val floor = acc + v
            if (floor == -1) return i + 1
            return@reduceIndexed floor
        }
    return -1
}

